import fetch from "node-fetch"
import * as collController from "../collection/controller.js"

import routes from "../../../shared/config/routes.js"

export const getHomePage = (req, res) => {
    const { lang } = req.cookies
    const tempLng = req.query?.lng
    req.i18n.changeLanguage(!tempLng ? lang || "ua" : tempLng)
    res.render('pages/home', {
        routes,
        pageId: req.url,
        activeLng: req.lng,
        full_url: req.url_lang,
        base_url: process.env.BASE_URL,
        ourLatestWork: [
            {
                image: "https://images03.nicepage.com/a1389d7bc73adea1e1c1fb7e/75e2f326ddfa5d2f90704173/pexels-photo-911758.jpeg"
            },
            {
                image: "data:image/jpeg;base64,"
            }, {
                image: "data:image/jpeg;base64,"
            },
            {
                image: "data:image/jpeg;base64,"
            }
        ]
    })
}

export const getContactPage = (req, res) => {
    const { lang } = req.cookies
    const tempLng = req.query?.lng
    req.i18n.changeLanguage(!tempLng ? lang || "ua" : tempLng)
    res.render('pages/contacts', { routes, pageId: req.url, activeLng: req.lng, full_url: req.url_lang, base_url: process.env.BASE_URL })
}

export const getAdminPanelPage = (req, res) => {
    res.render('pages/admin/admin-panel')
}

export const getCollectionsPage = async (req, res) => {
    const response = await fetch(`${process.env.BASE_URL}/api/collections`)
    const { collections } = await response.json()
    res.render('pages/admin/collections', { collections })
}